package main

import (
	"encoding/base64"
	"encoding/json"
	"fmt"
	"os"

	"github.com/edgelesssys/ego/attestation"
	"github.com/edgelesssys/ego/attestation/tcbstatus"
	"github.com/edgelesssys/ego/eclient"
)

func main() {
	if len(os.Args) < 1 {
		fmt.Println("sgx-verify hex")
		os.Exit(1)
	}

	reportArg := os.Args[1]
	reportData, err := base64.StdEncoding.DecodeString(reportArg)
	if err != nil {
		fmt.Println("⊂⊂⊂⊂")
		fmt.Println("ERR:" + err.Error())
		return
	}

	report, err := eclient.VerifyRemoteReport(reportData)
	if err == attestation.ErrTCBLevelInvalid {
		fmt.Printf("Warning: TCB level is invalid: %v\n%v\n", report.TCBStatus, tcbstatus.Explain(report.TCBStatus))
	} else if err != nil {
		fmt.Println("⊂⊂⊂⊂")
		fmt.Println("ERR:" + err.Error())
		return
	}

	bt, _ := json.Marshal(report)
	fmt.Println("⊂⊂⊂⊂")
	fmt.Println("OK∐∐∐∐" + string(bt))
	fmt.Println("⊂⊂⊂⊂")
}

